tinymce.PluginManager.add('example', function(editor, url) {
    // Add a button that opens a window
    editor.addButton('example', {
        text: '文件浏览器',
        icon: 'insert',
        tooltip: '资源浏览器',
        onclick: function() {
            editor.fileBrowser()
        }
    });

    return {
        getMetadata: function () {
            return  {
                name: "file browser",
                url: "http://exampleplugindocsurl.com"
            };
        }
    };
});
